import React, { useContext, useEffect, useState } from "react";
import { useLocation } from 'react-router-dom';
import SpotifyWebApi from 'spotify-web-api-js';
import { useGameContext } from './context/GameContext';
import { useNavigate } from 'react-router-dom';

const spotifyApi = new SpotifyWebApi();




function Game() {
    const [spotifyToken, setSpotifyToken] = useState(null);
    const [nowPlaying, setNowPlaying] = useState({});
    const [loggedIn, setLoggedIn] = useState(false);
    const { playlist, setPlaylist } = useGameContext();

    useEffect(() => {
        console.log("Playlist in Game.js: ", playlist);
      }, [playlist]);


    const play = () => {

        setNowPlaying({
                     name: playlist[0].name,
                     albumArt: playlist[0].album.images[0].url
                 });

        spotifyApi.play({uris: ["spotify:track:" + playlist[0].id]}).then(
            function(data) {
              console.log('Artist albums', data.body);
            },
            function(err) {
              if (err.status === 404) {
                alert('No Active Device');
              }
            });
    
    }

    return (
        <div className="Game">
                <button onClick={play}>Next</button>
                <div>Now Playing: {nowPlaying.name}</div>
                <div><img src={nowPlaying.albumArt} style={{ height: 150 }}/></div>
        </div>
    );
    
}
export default Game;
