import React, { useContext, useEffect, useState } from "react";
import { useLocation } from 'react-router-dom';
import SpotifyWebApi from 'spotify-web-api-js';
import { useGameContext } from './context/GameContext';
import { useNavigate } from 'react-router-dom';

const spotifyApi = new SpotifyWebApi();




const getTokenfromUrl = () => {
    return window.location.hash
    .substring(1)
    .split('&')
    .reduce((initial, item) => {
        let parts = item.split('=');
        initial[parts[0]] = decodeURIComponent(parts[1]);
        return initial;
    }, {});
}


function Login() {
    const [spotifyToken, setSpotifyToken] = useState(null);
    const [nowPlaying, setNowPlaying] = useState({});
    const [loggedIn, setLoggedIn] = useState(false);
    const { playlist, setPlaylist } = useGameContext();
    useEffect(() => {
        console.log("Playlist in Login.js: ", playlist);
      }, [playlist]);
    

    useEffect(() => {
        console.log("This is what we derived from the URL: ", getTokenfromUrl());
        const spotifyToken = getTokenfromUrl().access_token;
        window.location.hash = "";
        console.log("This is the token: ", spotifyToken);

        if (spotifyToken) {
            setSpotifyToken(spotifyToken);
            //use SpotifyWebApi to get access to Spotify API
            spotifyApi.setAccessToken(spotifyToken);
            setLoggedIn(true);
        }

    })


    const getNowPlaying = () => {
        console.log("This is the playlist: ", playlist);
        // spotifyApi.getMyCurrentPlaybackState()
        // .then((response) => {
        //     console.log("the id of the song is : " , response.item.id);
        //     setNowPlaying({
        //         name: response.item.name,
        //         albumArt: response.item.album.images[0].url
        //     });
        // })

        setNowPlaying({
                     name: playlist[0].name,
                     albumArt: playlist[0].album.images[0].url
                 });
        console.log("playlist[0] song id: ", playlist[0].id);
        const playTrack = async () => {
            try {
              // Attempt to play the track using the first track in the playlist
              await spotifyApi.play({ uris: ["spotify:track:" + playlist[0].id] });
              console.log("Track is now playing:", playlist[0].name);
            } catch (error) {
              // Handle the error if the API call fails
              console.error("Error occurred while trying to play the track:", error);
          
              // Check if error response is available
              if (error.response) {
                const { status, message } = error.response.data.error;
          
                // Log the error status and message
                console.log(`Error Status: ${status}`);
                console.log(`Error Message: ${message}`);
          
                // Handle specific error for no active device
                if (status === 404 && message.includes("No active device found")) {
                  alert("No active device found. Please make sure your Spotify app is open and connected.");
                }
              } else {
                // Handle other types of errors
                alert("An error occurred while trying to play the track. Please try again.");
              }
            }
          };
        
    }

    return (
        <div className="Login">
                {!loggedIn && <a href="http://localhost:8888">Login to Spotify</a>}
                {loggedIn && <button onClick={getNowPlaying}>Check Now Playing</button>}
                {loggedIn && <div>Now Playing: {nowPlaying.name}</div>}
                {loggedIn && <div><img src={nowPlaying.albumArt} style={{ height: 150 }}/></div>}
                
        </div>
    );
    
}
export default Login;
