import React, { useContext, useEffect, useState } from "react";
import { useLocation } from 'react-router-dom';
import SpotifyWebApi from 'spotify-web-api-js';
import { useGameContext } from './context/GameContext';
import { useNavigate } from 'react-router-dom';

const spotifyApi = new SpotifyWebApi();




function Game() {
    const [spotifyToken, setSpotifyToken] = useState(null);
    const [nowPlaying, setNowPlaying] = useState({});
    const [loggedIn, setLoggedIn] = useState(false);
    const { playlist, setPlaylist } = useGameContext();
    const [round, setRound] = useState(-1);
    const { currentUser, setCurrentUser } = useGameContext();
    const {playlistName, setPlaylistName} = useGameContext();
    const [gamePlaylist, setGamePlaylist] = useState(null);
    const [currentProgress, setCurrentProgress] = useState(0);


      // useEffect(() => {
      //   spotifyApi.getMyCurrentPlaybackState()
      //   .then(function(data) {
      //     console.log('CurrentPlaybackstate :', data);
      //     if (data.is_playing) {
      //       setNowPlaying({
      //         name: data.item.name,
      //         albumArt: data.item.album.images[0].url,
      //         progress_ms: data.progress_ms,
      //         duration_ms: data.item.duration_ms,
      //         });
      //       }
      //     }, 
      //     function(err) {
      //     console.log('Something went wrong in hte currentPlaybackstate', err);
      //     }
      //   )
      // });



    const play = () => {
        // Créer la playlist
        spotifyApi.createPlaylist(currentUser.id, {
          name: playlistName,
          public: false
        })
        .then(function(data) {
          console.log('Created playlist!', data);
          setGamePlaylist(data);

          // Ajouter les morceaux à la playlist
          spotifyApi.addTracksToPlaylist(data.id, playlist.map((song) => song.uri));
        })
        .then(function(data) {
          console.log('Added tracks to playlist!', data);
        })
        .catch(function(err) {
          console.log('Something went wrong creating the playlist!', err);
        })
        .catch(function(err) {
          console.log('Something went wrong adding tracks to the playlist!', err);
        });

        console.log("playlist URI : ", gamePlaylist.uri);

        setRound(round + 1);
        console.log('This is the gamePlaylist URI : ',gamePlaylist.uri);
        spotifyApi.play({
          context_uri: gamePlaylist.uri
        }).then(function(data) {
          console.log('Playing playlist!', data);
        })
        .catch(function(err) {
          console.log('Something went wrong playing the playlist!', err);
        });
      };

    const skip = () => {
      setRound(round + 1);
    }

    return (
      <div className="Game">
      {round === -1 ? (
        <button onClick={play}>Play</button>
      ) : (
        <>
          <button onClick={skip}>Next</button>
          <div>Now Playing: {nowPlaying.name}</div>
          <div><img src={nowPlaying.albumArt} style={{ height: 150 }}/></div>
        </>
      )}
    </div>
    );
    
}
export default Game;
