import React, { useContext, useEffect, useState } from "react";
import { useLocation } from 'react-router-dom';
import SpotifyWebApi from 'spotify-web-api-js';
import { useGameContext } from './context/GameContext';
import { useNavigate } from 'react-router-dom';

const spotifyApi = new SpotifyWebApi();




function Game() {
    const [spotifyToken, setSpotifyToken] = useState(null);
    const [nowPlaying, setNowPlaying] = useState({});
    const [loggedIn, setLoggedIn] = useState(false);
    const { playlist, setPlaylist } = useGameContext();
    const [round, setRound] = useState(0);
    const [currentUser, setCurrentUser] = useState(null);

    useEffect(() => {
        console.log("Playlist in Game.js: ", playlist);
      }, [playlist]);


    const play = () => {

  spotifyApi
  .getUserPlaylists() // note that we don't pass a user id
  .then(
    function (data) {
      console.log('User playlists', data);
    },
    function (err) {
      console.error(err);
    }
  );

      spotifyApi.createPlaylist("j65mmrwflphtbvv3wklvilh0n", {name: "My New Playlist", public: false})
      .then(function(data) {
        console.log('Created playlist!', data);
      }, function(err) {
        console.log('Something went wrong!', err);

      
      });
        setNowPlaying({
                     name: playlist[round].name,
                     albumArt: playlist[round].album.images[round].url
                 });

        spotifyApi.play({uris: ["spotify:track:" + playlist[round].id]}).then(
            function(data) {
              console.log('Artist albums', data.body);
              setRound(round + 1);

            },
            function(err) {
              if (err.status === 404) {
                alert('No Active Device');
              }
            });
    }

    return (
        <div className="Game">
                <button onClick={play}>Next</button>
                <div>Now Playing: {nowPlaying.name}</div>
                <div><img src={nowPlaying.albumArt} style={{ height: 150 }}/></div>
        </div>
    );
    
}
export default Game;
