import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [players, setPlayers] = useState([]);
  const [currentPlayer, setCurrentPlayer] = useState('');
  const [song, setSong] = useState('');
  const [playlistId, setPlaylistId] = useState('YOUR_SPOTIFY_PLAYLIST_ID');
  const [accessToken, setAccessToken] = useState('YOUR_SPOTIFY_ACCESS_TOKEN');
  const [phase, setPhase] = useState(1); // Phase 1: Entrée des joueurs, Phase 2: Saisie des chansons
  const [currentPlayerIndex, setCurrentPlayerIndex] = useState(0);

  const addPlayer = () => {
    if (currentPlayer) {
      setPlayers([...players, currentPlayer]);
      setCurrentPlayer('');
    }
  };

  const nextPhase = () => {
    setPhase(2);
  };

  const addSong = () => {
    if (song) {
      axios.post('/add-song', { song, playlistId, accessToken })
        .then(response => {
          setSong('');
          setCurrentPlayerIndex((prevIndex) => (prevIndex + 1) % players.length);
        })
        .catch(error => console.error(error));
    }
  };

  return (
    <div>
      {phase === 1 ? (
        <div>
          <h1>Ajouter des joueurs</h1>
          <input type="text" value={currentPlayer} onChange={(e) => setCurrentPlayer(e.target.value)} />
          <button onClick={addPlayer}>Ajouter joueur</button>
          <h2>Joueurs :</h2>
          <ul>
            {players.map((player, index) => (
              <li key={index}>{player}</li>
            ))}
          </ul>
          {players.length > 0 && (
            <button onClick={nextPhase}>Commencer à ajouter des chansons</button>
          )}
        </div>
      ) : (
        <div>
          <h1>Ajouter des chansons</h1>
          <h2>{players[currentPlayerIndex]}, entrez une chanson :</h2>
          <input type="text" value={song} onChange={(e) => setSong(e.target.value)} />
          <button onClick={addSong}>Ajouter chanson</button>
        </div>
      )}
    </div>
  );
}

export default App;
