import React, { useContext, useEffect, useState } from "react";
import { useLocation } from 'react-router-dom';
import SpotifyWebApi from 'spotify-web-api-js';
import { useGameContext } from './context/GameContext';
import { useNavigate } from 'react-router-dom';

const spotifyApi = new SpotifyWebApi();




function Game() {
    const [spotifyToken, setSpotifyToken] = useState(null);
    const [nowPlaying, setNowPlaying] = useState({});
    const [loggedIn, setLoggedIn] = useState(false);
    const { playlist, setPlaylist } = useGameContext();
    const [round, setRound] = useState(-1);
    const { currentUser, setCurrentUser } = useGameContext();
    const {playlistName, setPlaylistName} = useGameContext();
    const {gamePlaylist, setGamePlaylist} = useGameContext();
    const {playerList, setPlayerList} = useGameContext();
    const [replay, setReplay] = useState(false);

    const navigate = useNavigate();

      useEffect(() => {
        setGamePlaylist(gamePlaylist.concat(playlist));
        console.log("GamePlaylist : ", gamePlaylist);
      }, []);

      useEffect(() => {
        setPlaylistName((prevPlaylistName) => {
          for (let playerName of playerList) {
            const newPlaylistName = prevPlaylistName  + playerName.trim().slice(0, 3);
            return newPlaylistName;
          }
      });
    }, [playlistName]);


      useEffect(() => {
        const intervalId = setInterval(getNowPlaying, 5000);
        return () => clearInterval(intervalId);

      }, []);

      useEffect(() => {
        console.log("nowPlaying.progress_ms = ", nowPlaying.progress_ms); 
        console.log("nowPlaying.duration_ms = ", nowPlaying.duration_ms);
        console.log("Playlist : ", playlist);
        if (nowPlaying.progress_ms >= nowPlaying.duration_ms || (nowPlaying.progress_ms == 0 && nowPlaying.is_playing == false && round != -1)) {
          skip();
        }
      },[nowPlaying]);  

      const getNowPlaying = () => {
          spotifyApi.getMyCurrentPlaybackState()
          .then(function(data) {
            console.log('CurrentPlaybackstate :', data);
            if (data) {
              setNowPlaying({
                name: data.item.name,
                albumArt: data.item.album.images[0].url,
                progress_ms: data.progress_ms,
                duration_ms: data.item.duration_ms,
                uri: data.item.uri,
                is_playing: data.is_playing,
                });
              }
            }, 
            function(err) {
            console.log('Something went wrong in hte currentPlaybackstate', err);
            }
          )
        }

        const savePlaylist = () => {
                    // Créer la playlist
          spotifyApi.createPlaylist(currentUser.id, {
            name: playlistName,
            public: false
          })
          .then(function(data) {
            console.log('Created playlist!', data);

            // Ajouter les morceaux à la playlist
            spotifyApi.addTracksToPlaylist(data.id, gamePlaylist.map((song) => song.uri))

          .then(function(data) {
            console.log('Added tracks to playlist!', data);
          })
          .catch(function(err) {
            console.log('Something went wrong creating the playlist!', err);
          })
          .catch(function(err) {
            console.log('Something went wrong adding tracks to the playlist!', err);
            });
          });
        }

        function play(track) {
          // Vérifier si un ID de morceau est fourni
          if (!track) {
            console.log('No song ID provided');
            return Promise.resolve(1); // Return a resolved promise with value 1
          }
        
          // Appel à l'API Spotify pour jouer un morceau sur un appareil actif
          return spotifyApi.play({
            uris: [track.uri]
          })
          .then(function() {
            console.log('Playback started for song ID:', track);
            playlist.splice(playlist.indexOf(track), 1);
            setRound(round + 1);
        
            // Use setTimeout to handle playback duration and calling next step
            setTimeout(() => {
              getNowPlaying();  // Appelle la fonction next après que la durée soit écoulée
            }, track.duration_ms);
        
            return 0;  // Return 0 to indicate success
          })
          .catch(function(err) {
            if (err.status === 404 && err.responseText.includes("NO_ACTIVE_DEVICE")) {
              alert('No active device found to play the track.');
              return 1; // Return 1 if no active device is found
            } else {
              console.log('Something went wrong while trying to play the track:', err);
              return 1;  // Return 1 for other errors
            }
          });
        }


    const start = () => {

      const randint = Math.floor(Math.random() * playlist.length);
      play(playlist[randint]);
      getNowPlaying();
      



     }
    

    const skip = () => {

        if (playlist.length != 0) {                              //track == nowPlaying || track == null
          console.log('SONG SKIPPED');
          const randint = Math.floor(Math.random() * playlist.length);
          play(playlist[randint]);
        }
        else 
          exit();
        
    }
    const exit = () => {
        setReplay(true);
        spotifyApi.pause()
        .then(function() {
          console.log('Playback paused');
        })
        .catch(function(err) {
          console.log('Something went wrong while trying to pause the playback:', err);
        });

      }
    const handleReplay = () => {
        navigate('/song-selection');
      }
    const handleLeave = () => {
        // Quitter le jeu
      }


      return (
        <div className="Game">
          {round === -1 ? (
            <button onClick={start}>Play</button>
          ) : (
            <>
              {replay ? (
                <>
                  <button onClick={handleReplay}>Replay</button>
                  <button onClick={handleLeave}>Leave</button>
      
                  {/* Ajouter le champ pour nommer la playlist */}
                  <div style={{ marginTop: '20px' }}>
                    <label htmlFor="playlistName">Playlist name: </label>
                    <input
                      id="playlistName"
                      type="text"
                      value={playlistName}
                      onChange={(e) => setPlaylistName(e.target.value)}
                      placeholder="MyPlaylist"
                    />
                  </div>
      
                  {/* Bouton pour sauvegarder la playlist */}
                  <button onClick={() => savePlaylist(playlistName)}>
                    Save Playlist
                  </button>
                </>
              ) : (
                <>
                  <button onClick={skip}>Next</button>
                  <div>Now Playing: {nowPlaying.name}</div>
                  <div>
                    <img src={nowPlaying.albumArt} style={{ height: 150 }} alt={nowPlaying.name} />
                  </div>
                </>
              )}
            </>
          )}
        </div>
      );
    
}
export default Game;
