import React, { useContext, useEffect, useState } from "react";
import Axios from "axios";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container, InputGroup, FormControl, Row, Card, Button} from 'react-bootstrap';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { GameContext } from '../context/GameContext';

const CLIENT_ID = "e9f2d6bd6f3a4badbd019e27348cc60b";
const CLIENT_SECRET = "af29a25fdd984740bf53174f77c78b13";

const SongSelection = () => {
  const [data, setData] = useState("");
  const [searchInput, setSearchInput] = useState("");
  const [accessToken, setAccessToken] = useState("");
  const [tracks, setTracks] = useState([]);
  const { playlist, setPlaylist } = useContext(GameContext);
  const navigate = useNavigate();



  const getData = async () => {
    const response = await Axios.get("http://localhost:3000/getData");
    setData(response.data);
  }

  useEffect(() => {
    var authParameters = {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: 'grant_type=client_credentials&client_id=' + CLIENT_ID + '&client_secret=' + CLIENT_SECRET
    }
    fetch("https://accounts.spotify.com/api/token", authParameters)
      .then(result => result.json())
      .then(data => setAccessToken(data.access_token))
      .catch(error => console.log(error))

    // getData();
  }, []); 

  useEffect(() => {
    if (searchInput.trim() === '') return; // Do nothing if searchInput is empty
    search();
  }, [searchInput]); // Dependency array includes searchInput


  //Search
  async function search() {
    console.log("searching for: " + searchInput);

    // Get request using seach to get artistID
    var searchParameters = {
      method: "GET",
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + accessToken
      }
    }

    var tracks = await fetch('https://api.spotify.com/v1/search?q=' + searchInput + '&type=track' , searchParameters)
      .then(response => response.json())

      .then(data => {
        console.log(data)
        setTracks(data.tracks.items)
      })
      .catch(error => console.log(error))





    // Get request with Artst ID grab all the albums from taht artist

  //   console.log("Track ID is : " + songID);
  //   var albums = await fetch('https://api.spotify.com/v1/tracks/' + songID + '/albums' + '?include_groups=album&limit=50', searchParameters)
  //     .then(response => response.json())
  //     .then(data => { 
  //       console.log(data)
  //       setAlbums(data.items)
  //     });
  }

  const handleCardClick = (track) => {
    console.log("Card clicked:", track);
    setPlaylist([...playlist, track]);
    console.log("Playlist: ", playlist);
    // Add logic to handle the click event, such as navigating to a new page or playing a song
  };

  const playButtonHandler = () => {
    console.log("Play button clicked");
    console.log("Playlist: ", playlist);
    navigate('/login', { state: {playlist} });
  };


    // Display all the albums





    return (

        <div className="App">
        <Container>
          <InputGroup className="mb-3" size="lg">
            <FormControl
              placehloder="Search For Artist"
              type="input"
              onKeyPress={(e) => {
                if (e.key === 'Enter') {
                  console.log("pressed enter");
                }
              }}
              onChange={(e) => {
                setSearchInput(e.target.value);
              }}
            ></FormControl>
            <button onClick={playButtonHandler}>Play</button>
          </InputGroup>
        </Container>

        <Container>
          <Row className="mx-2 row row-cols-4">
            {tracks.map( (track,i) => {
              return (
                <Card onClick={() => handleCardClick(track)} className="clickable-card">
                  <Card.Img src={track.album.images[0].url}/>
                  <Card.Body>
                    <Card.Title>{track.name}</Card.Title>
                    <Card.Text>
                      {track.artists[0].name}
                    </Card.Text>

                  </Card.Body>
                </Card>
              )
            })}

          </Row>
        </Container>


        </div>
    );


}
export default SongSelection
