import React, { useContext, useEffect, useState } from "react";
import { useLocation } from 'react-router-dom';
import SpotifyWebApi from 'spotify-web-api-js';
import { useGameContext } from './context/GameContext';
import { useNavigate } from 'react-router-dom';
import Game from './Game';
var spotifyApi = new SpotifyWebApi(); 



const getTokenfromUrl = () => {
    return window.location.hash
    .substring(1)
    .split('&')
    .reduce((initial, item) => {
        let parts = item.split('=');
        initial[parts[0]] = decodeURIComponent(parts[1]);
        return initial;
    }, {});
}


function Login() {
    const [spotifyToken, setSpotifyToken] = useState(null);
    const [nowPlaying, setNowPlaying] = useState({});
    const [loggedIn, setLoggedIn] = useState(false);
    const { playlist, setPlaylist } = useGameContext();

    useEffect(() => {
        console.log("Playlist in Login.js: ", playlist);
      }, [playlist]);
    

    useEffect(() => {
        console.log("This is what we derived from the URL: ", getTokenfromUrl());
        const spotifyToken = getTokenfromUrl().access_token;
        window.location.hash = "";
        console.log("This is the token: ", spotifyToken);

        if (spotifyToken) {
            setSpotifyToken(spotifyToken);
            //use SpotifyWebApi to get access to Spotify API
            spotifyApi.setAccessToken(spotifyToken);
            console.log("This is the token IN THE IF: ", spotifyToken);
            spotifyApi.getMe()
      .then(function(data) {
        console.log('Some information about the authenticated userIN  THE IF', data.body);
      }, function(err) {
        console.log('Something went wrong USER! IN  THE IF', err);
      });
            setLoggedIn(true);
        }

    });

    const getNowPlaying = () => {

        setNowPlaying({
                     name: playlist[0].name,
                     albumArt: playlist[0].album.images[0].url
                 });

        spotifyApi.play({uris: ["spotify:track:" + playlist[0].id]}).then(
            function(data) {
              console.log('Artist albums', data.body);
            },
            function(err) {
              if (err.status === 404) {
                alert('No Active Device');
              }
            });
    
    }

    return (
        <div className="Login"> 
        {loggedIn ? (
        <Game /> // Render Game component if loggedIn is true
      ) : (
        <a href="http://localhost:8888">Login to Spotify</a> // Render login link if loggedIn is false
      )}
    </div>
    );
    
}
export default Login;
