import React, { useEffect, useState } from "react";
import Axios from "axios";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Container, InputGroup, FormControl, Row, Card, Button} from 'react-bootstrap';


const CLIENT_ID = "e9f2d6bd6f3a4badbd019e27348cc60b";
const CLIENT_SECRET = "af29a25fdd984740bf53174f77c78b13";

const App = () => {
  const [data, setData] = useState("");
  const [searchInput, setSearchInput] = useState("");
  const [accessToken, setAccessToken] = useState("");

  const getData = async () => {
    const response = await Axios.get("http://localhost:3000/getData");
    setData(response.data);
  }

  useEffect(() => {
    var authParameters = {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: 'grant_type=client_credentials&client_id=' + CLIENT_ID + '&client_secret=' + CLIENT_SECRET
    }
    fetch("https://accounts.spotify.com/api/token", authParameters)
      .then(result => result.json())
      .then(data => setAccessToken(data.access_token))
      .catch(error => console.log(error))

    // getData();
  }, []); 

  //Search
  async function search() {
    console.log("searching for: " + searchInput);

    // Get request using seach to get artistID
    var SearchParameters = {
      method: "GET",
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + accessToken
      }
    }

    var artistID = await fetch('https://api.spotify.com/v1/search?q=' + searchInput + '&type=artist' , SearchParameters)
      .then(response => response.json())
      .then(data => { return data.artists.items[0].id})
      .catch(error => console.log(error))





    // Get request with Artst ID grab all the albums from taht artist

    console.log("Arist ID is : " + artistID);
    var albums = await fetch('https://api.spotify.com/v1/artists/' + artistID + '/albums' + '?include_groups=limit=5', SearchParameters)
      .then(response => response.json())
      .then(data => { return data.items})
      .catch(error => console.log(error))




    // Display all the albums

  }



    return (

        <div className="App">
        <Container>
          <InputGroup className="mb-3" size="lg">
            <FormControl
              placehloder="Search For Artist"
              type="input"
              onKeyPress={(e) => {
                if (e.key === 'Enter') {
                  console.log("pressed enter");
                }
              }}
              onChange={(e) => setSearchInput(e.target.value)}
            ></FormControl>
            <Button
              onClick={search}>
              Search
            </Button>
          </InputGroup>
        </Container>

        <Container>
          <Row className="mx-2 row row-cols-4">
            <Card>
              <Card.Body>
                <Card.Title>Album Name</Card.Title>
                  <Card.Text>
                    Some quick example text to build on the card title and make up the bulk of the card's content.
                  </Card.Text>
                <Button variant="primary">Go somewhere</Button>
              </Card.Body>
            </Card>

          </Row>
        </Container>


        </div>
    );

}
export default App
